import React, { useState } from 'react';
import PostItem from '../components/PostItem';

import Thumbnail1 from '../images/blog1.jpg';
import Thumbnail2 from '../images/blog2.jpg';
import Thumbnail3 from '../images/blog3.jpg';
import Thumbnail4 from '../images/blog4.jpg';

const DUMMY_POSTS = [
    {
        id: '1',
        thumbnail: Thumbnail1,
        category: 'education',
        title: 'This is the title of the very first post on this blog',
        desc: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
        authorID: 3,
    },
    {
        id: '2',
        thumbnail: Thumbnail2,
        category: 'science',
        title: 'This is the title of the very second post on this blog',
        desc: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
        authorID: 1,
    },
    {
        id: '3',
        thumbnail: Thumbnail3,
        category: 'weather',
        title: 'This is the title of the very third post on this blog',
        desc: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
        authorID: 13,
    },
    {
        id: '4',
        thumbnail: Thumbnail4,
        category: 'farming',
        title: 'This is the title of the very last post on this blog',
        desc: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
        authorID: 11,
    },
];

const Posts = () => {
    const [posts] = useState(DUMMY_POSTS);

    return (
        <section className="posts">
            {posts.map(({ id, thumbnail, category, title, desc, authorID }) => (
                <PostItem
                    key={id}
                    thumbnail={thumbnail}
                    category={category}
                    title={title}
                    description={desc} // Cambiado de `description` a `desc`
                    authorID={authorID}
                />
            ))}
        </section>
    );
};

export default Posts;
