import React from 'react'
import {Link} from 'react-router-dom'
import Avatar from '../images/avatar1.jpg'

const PostAuthor = () => {
    return (
        <Link to = {`/posts/users/sdfsdf`}>
            <div className="post__author-avatar">
                <img src={Avatar} alt="" />
            </div>
        </Link>
    )
}
export default PostAuthor
